nvars($_POST['subject']));     if ($system->SETTINGS['wordsfilter'] == 'y')     {         $cleaned_question = stripslashes($system->filter($cleaned_question));         $cleaned_subject = stripslashes($system->filter($cleaned_subject));         }    	// Check errors	if (isset($_POST['action']) && (!filter_var($_POST['sender_email'], FILTER_VALIDATE_EMAIL) || !filter_var($admin_email, FILTER_VALIDATE_EMAIL)))     {    	$TPL_error_text = $ERR['032'];	}	elseif (empty($cleaned_question))	{		$TPL_error_text = $ERR['031'];	}	elseif (empty($cleaned_subject))     {         $TPL_error_text = $ERR['031'];     } 	elseif (isset($_POST['action']) && (!filter_var($_POST['sender_email'], FILTER_VALIDATE_EMAIL) || !filter_var($admin_email, FILTER_VALIDATE_EMAIL) || !filter_var($sender_email, FILTER_VALIDATE_EMAIL)))     {    	$TPL_error_text = $ERR['008'];	}	elseif ($system->SETTINGS['spam_register'] == 2 && !$resp)	{    	$TPL_error_text = $MSG['752'];	}	elseif ($system->SETTINGS['spam_register'] == 1 && !$resp->check($_POST['captcha_code']))	{		$TPL_error_text = $MSG['752'];	}	if (empty($TPL_error_text))	{		$senders_name = isset($_POST['sender_name']) ? $system->cleanvars($_POST['sender_name']) : '';		$senders_email = $sender_email !='' && isset($_POST['sender_email']) ? $system->cleanvars($_POST['sender_email']) : '';		$mes = $send_email->email_request_support($senders_name, $cleaned_question, $cleaned_subject, $senders_email, $admin_nick, 0, $admin_email, $sender_email);	}}$template->assign_vars(array(	'MESSAGE' => (isset($mes)) ? $mes : '',	'ERROR' => (isset($TPL_error_text)) ? $TPL_error_text : '',	'ADMIN_NICK' => $admin_nick,	'ADMIN_EMAIL' => $system->SETTINGS['adminmail'],	'SELLER_QUESTION' => $_POST['sender_question'] !='' ? $CKEditor->editor('TPL_text', $_POST['sender_question']) : $CKEditor->editor('sender_question', ''),	'PAGE_TITLE' => $page_title,	'B_FB_LINK' => 'IndexFBLogin',	'CAPCHA' => ($system->SETTINGS['spam_register'] == 2) ? $capcha_text : $spam_html,	'SENDER_EMAIL' => isset($_POST['sender_email']) ? $_POST['sender_email'] : '',	'SENDER_NAME' => isset($_POST['sender_name']) ? $_POST['sender_name'] : '',	'SUBJECT' => isset($_POST['subject']) ? $system->cleanvars($_POST['subject']) : '',	'EMAIL' => isset($user->user_data['email']) ? $user->user_data['email'] : ''));include 'header.php';$template->set_filenames(array(		'body' => 'email_request_support.tpl'		));$template->display('body');include 'footer.php';