	elseif ($row['type'] == 'signup_fee')	{		$template->assign_vars(array(			'B_SIGNUP_FEE' => ($row['value'] > 0),			'SIGNUP_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'hpfeat_fee')	{		$template->assign_vars(array(			'B_HPFEAT_FEE' => ($row['value'] > 0),			'HPFEAT_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'bolditem_fee')	{		$template->assign_vars(array(			'B_BOLD_FEE' => ($row['value'] > 0),			'BOLD_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'hlitem_fee')	{		$template->assign_vars(array(			'B_HL_FEE' => ($row['value'] > 0),			'HL_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'rp_fee')	{		$template->assign_vars(array(			'B_RP_FEE' => ($row['value'] > 0),			'RP_FEE' => $system->print_money($row['value'])			));	}	elseif ($row['type'] == 'picture_fee')	{		$template->assign_vars(array(			'B_PICTURE_FEE' => ($row['value'] > 0),			'PICTURE_FEE' => $system->print_money($row['value']),			'FREE_PICTURES' => $system->SETTINGS['freemaxpictures'] > 0 ? sprintf($MSG['3500_1015769'], $system->SETTINGS['freemaxpictures']) : ''		));	}	elseif ($row['type'] == 'relist_fee')	{		$template->assign_vars(array(			'B_RELIST_FEE' => ($row['value'] > 0),			'RELIST_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'buyout_fee')	{		$template->assign_vars(array(			'B_BUYNOW_FEE' => ($row['value'] > 0),			'BUYNOW_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'excat_fee')	{		$template->assign_vars(array(			'B_EXCAT_FEE' => ($row['value'] > 0),			'EXCAT_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'subtitle_fee')	{		$template->assign_vars(array(			'B_SUBTITLE_FEE' => ($row['value'] > 0),			'SUBTITLE_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'banner_fee')	{		$template->assign_vars(array(			'B_BANNER_FEE' => ($row['value'] > 0),			'BANNER_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'ex_banner_fee')	{		$template->assign_vars(array(			'B_EX_BANNER_FEE' => ($row['value'] > 0),			'EX_BANNER_FEE' => $system->print_money($row['value'])		));	}	elseif ($row['type'] == 'geomap_fee')	{		$template->assign_vars(array(			'B_GOOGLE_MAP_FEE' => ($row['value'] > 0),			'GOOGLE_MAP_FEE' => $system->print_money($row['value'])		));	}	$i++;}$template->assign_vars(array(	'B_SETUP_FEE' => $setup,	'B_FB_LINK' => 'IndexFBLogin',	'B_BUYER_FEE' => $buyer_fee,	'B_ENDAUC_FEE' => $endauc_fee,	'BONUS' => $system->print_money($system->SETTINGS['fee_signup_bonus']),	'B_BONUS_FEE' => $system->SETTINGS['fee_signup_bonus'] > 0 ? true : false));include 'header.php';$template->set_filenames(array(		'body' => 'fees.tpl'		));$template->display('body');include 'footer.php';