_fee')	{			$template->assign_vars(array(				'B_EX_BANNER_FEE' => ($row['value'] > 0),				'EX_BANNER_FEE' => $system->print_money($row['value'])				));	}}$template->assign_vars(array(	'ERROR' => (isset($ERROR)) ? $ERROR : '',	'ID' => $id,	'NAME' => $USER['name'],	'COMPANY' => $USER['company'],	'EMAIL' => $USER['email'],	'NOTEDIT' => true,	'ACTIVEACCOUNTTAB' => 'class="active"',	'ACTIVEMYADVERTISMENT' => 'class="active"',	'ACTIVEACCOUNTPANEL' => 'active'));include 'header.php';$template->set_filenames(array(		'body' => 'userbanners.tpl'		));$template->display('body');include 'footer.php';