$DBPrefix . "faqs_translated t ON (t.faq_id = f.id)			WHERE f.category = :cat AND t.lang = :languages";	$params = array(		array(':cat', $cat, 'int'),		array(':languages', $language, 'int')	);	$db->query($query, $params);	while ($row = $db->result())	{		if (!empty($row['question']) && !empty($row['answer']))		{			$question = stripslashes($row['question']);			$answer = stripslashes($row['answer']);		}		else		{			$question = stripslashes($row['q']);			$answer = stripslashes($row['a']);		}		$template->assign_block_vars('faqs', array(				'Q' => $question,				'A' => $answer,				'ID' => $row['faq_id']				));	}	$jsfiles = 'js/jquery.js;';	$template->assign_vars(array(		'JSFILES' => $jsfiles	));	$template->set_filenames(array(			'body' => 'viewhelp.tpl'			));	$template->display('body');}else{	header('location: help.php');}