while ($arrfeed = $db->result()){	$query = "SELECT id, rate_num, rate_sum FROM " . $DBPrefix . "users WHERE nick = :user_nick";	$params = array(		array(':user_nick', $arrfeed['rater_user_nick'], 'str')	);	$db->query($query, $params);	$usarr = $db->result();	$j = 0;	foreach ($memtypesarr as $k => $l)	{		if ($k >= $usarr['rate_sum'] || $j++ == (count($memtypesarr) - 1))		{			$usicon = '<img src="' . $system->SETTINGS['siteurl'] . 'images/icons/' . $l['icon'] . '" alt="' . $l['icon'] . '" class="fbstar">';			break;		}	}	switch ($arrfeed['rate'])	{		case 1: $uimg = $system->SETTINGS['siteurl'] . 'images/positive.png';			break;		case - 1: $uimg = $system->SETTINGS['siteurl'] . 'images/negative.png';			break;		case 0: $uimg = $system->SETTINGS['siteurl'] . 'images/neutral.png';			break;	}	$template->assign_block_vars('fbs', array(			'BGCOLOR' => (!(($i + 1) % 2)) ? '' : 'class="alt-row"',			'IMG' => $uimg,			'USFLINK' => 'profile.php?user_id=' . $usarr['id'] . '&auction_id=' . $arrfeed['auction_id'],			'USERNAME' => $arrfeed['rater_user_nick'],			'USFEED' => $usarr['rate_sum'],			'USICON' => (isset($usicon)) ? $usicon : '',			'FBDATE' => $system->dateToTimestamp($arrfeed['feedbackdate']),			'AUCTIONURL' => ($arrfeed['title']) ? '<a href="' . $system->SETTINGS['siteurl'] . 'products/' . $arrfeed['title'] . '-' . $arrfeed['auction_id'] . '">' . $arrfeed['title'] . '</a>' : $MSG['113'] . $arrfeed['auction_id'],			'FEEDBACK' => nl2br(stripslashes($arrfeed['feedback']))			));	$i++;}// get pagenation$PREV = intval($PAGE - 1);$NEXT = intval($PAGE + 1);if ($PAGES > 1){	$LOW = $PAGE - 5;	if ($LOW <= 0) $LOW = 1;	$COUNTER = $LOW;	while ($COUNTER <= $PAGES && $COUNTER < ($PAGE + 6))	{		$template->assign_block_vars('pages', array(				'PAGE' => ($PAGE == $COUNTER) ? '<li class="disabled"><a href="#">' . $COUNTER . '</a></li>' : '<li><a href="' . $system->SETTINGS['siteurl'] . 'yourfeedback.php?PAGE=' . $COUNTER . '">' . $COUNTER . '</a></li>'				));		$COUNTER++;	}}$template->assign_vars(array(	'USERNICK' => $user->user_data['nick'],	'USERFB' => $user->user_data['rate_sum'],	'USERFBIMG' => (isset($TPL_rate_ratio_value)) ? $TPL_rate_ratio_value : '',	'PREV' => ($PAGES > 1 && $PAGE > 1) ? '<a href="' . $system->SETTINGS['siteurl'] . 'yourfeedback.php?PAGE=' . $PREV . '">' . $MSG['5119'] . '</a>' : '',	'NEXT' => ($PAGE < $PAGES) ? '<a href="' . $system->SETTINGS['siteurl'] . 'yourfeedback.php?PAGE=' . $NEXT . '">' . $MSG['5120'] . '</a>' : '',	'PAGE' => $PAGE,	'PAGES' => $PAGES,	'BGCOLOR' => (!(($i + 1) % 2)) ? '' : 'class="alt-row"',	'ACTIVEACCOUNTTAB' => 'class="active"',	'ACTIVEVIEWFEEDBACK' => 'class="active"',	'ACTIVEACCOUNTPANEL' => 'active'));include 'header.php';$template->set_filenames(array(		'body' => 'yourfeedback.tpl'		));$template->display('body');include 'footer.php';